import { Component, OnInit } from '@angular/core';
import { DataService } from '../data.service';

@Component({
  selector: 'app-liste',
  templateUrl: './liste.component.html',
  styleUrls: ['./liste.component.css']
})
export class ListeComponent implements OnInit {

  message: string = "";
  liste;

  constructor (private dataService:DataService) {}
    ngOnInit() {
      this.liste = this.dataService.listeArticles;
    }

  onAffiche(arg: string) {
    return this.message = "Merci d'avoir vôté pour l'article : " + arg;
  }
}
<div class="container">
    <div id="alertMsg">
      <p>
        {{ message }}
      </p>
    </div>
    <div id="home">

      <app-article 
        *ngFor = "let element of liste"
        [titreArticle]="element.titreArticle" 
        [prixArticle]="element.prixArticle" 
        [description]="element.description"
        [urlImg]="element.urlImg"
        [textAltImg]="element.textAltImg"
        [dispo]="element.dispo"
        (info)="onAffiche($event)">
      </app-article>
    </div>
  </div>
