import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-ajouter',
  templateUrl: './ajouter.component.html',
  styleUrls: ['./ajouter.component.css']
})
export class AjouterComponent implements OnInit {
  titleToAdd = '';
  priceToAdd = '';

  constructor() { }

  ngOnInit(): void {

  }

  //Méthode qui affichera les const:
  onSubmit(form: NgForm) {
    const newTitle = this.titleToAdd;
    const newPrice = this.priceToAdd;
    console.log("newTitle : " + newTitle);
    console.log("newPrice : " + newPrice);
  }
}
<h1>Ajouter un nouvel article</h1>

<form (ngSubmit)="onSubmit(f)" #f="ngForm">

    <label for="">Titre</label>
    <input type="text" name="title" [(ngModel)]="titleToAdd" required minlength="4">
    <div>
        <div>
            Titre obligatoire
        </div>
    </div>
    <br>

    <label for="">Prix</label>
    <input type="number" name="price" [(ngModel)]="priceToAdd" required>

    <br>

    <button class="btn btn-primary" type="submit">Envoyer</button>
</form>
