import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-article',
  templateUrl: './article.component.html',
  styleUrls: ['./article.component.css']
})
export class ArticleComponent implements OnInit {

totalNbLike: number = 0;
comment: string = "Ceci est un commentaire";


//Permet de personnaliser les prp des components
@Input() titreArticle: string;
@Input() prixArticle: number;
@Input() description: string;
@Input() urlImg: string;
@Input() textAltImg: string;
@Input() dispo: boolean;

//Permet de transmettre des infos vers le parent:
@Output() info = new EventEmitter<string>();

  constructor() { }

  ngOnInit(): void {
      }

  //Code de l'event click:    
  onLike() {
    this.totalNbLike++;
    this.info.emit(this.titreArticle);
  }

}
<div class="card articleInfo">
    <!--Property binding permet de récupérer grâce aux crochets les val contenues ds article.comp.ts pr: src et alt-->
    <img [src]="urlImg" [alt]="textAltImg">
    <div class="card-body">
        <h5 class="card-title"> 
            {{ titreArticle }} 
            <span *ngIf="dispo === false"> - Indisponible</span>
        </h5>
        <h6 class="card-title"> {{ prixArticle }} &euro;</h6>
        <p class="card-text">Lorem ipsum dolor sit amet consectetur adipisicing elit. Cupiditate, quidem!</p>

        <button class="btn btn-primary" (click)="onLike()">Me gusta</button>
        <p>Nb like : {{ totalNbLike }} </p>

        <!--Two-way binding va permettre de renvoyer des infos vers le TypeScript-->
        <input type="text" placeholder="Ecris un commentaire" [(ngModel)]="comment">
        <p>COMMENTAIRE : {{ comment }} </p>
    </div>
</div>
