import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DataService } from '../data.service';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent implements OnInit {
  id;
  titre: number;
  prix: number;
  description: number;
  urlImg: number;

  constructor (
    private route: ActivatedRoute,
    private dataService: DataService,
  ) {}

  ngOnInit() {
      const id = this.route.snapshot.params["id"];
      this.id = id;
      this.titre = this.dataService.getArticle(id).prixArticle;
      this.prix = this.dataService.getArticle(id).prixArticle;
      this.description = this.dataService.getArticle(id).prixArticle;
      this.urlImg = this.dataService.getArticle(id).prixArticle;
  }
}
<div id="infoDetails" class="container">
    <img [src]="urlImg">
    <h1> {{ titre }} </h1>
    <h3> {{ prix }}  &euro;</h3>
    <p> {{ description  }} </p>
</div>
